#! /usr/bin/perl -w

#This program finds the largest multiple of any 13 consecutive numbers.

use strict;
use warnings;

my $x = "";

my $i = undef;
my $j = undef;
my @arr;
my $final = 0;

sub prod {
	$i = shift;
	$j = shift;
	@arr = split(//, $x);
	for(my $z = 0; $z <= $#arr -$j; $z++){
		my $k = $arr[$i];
		my $m = $arr[$j];
#		print "k and m are: $k and $m\n";
		my $multiple=1;
		foreach my $index ($i..$j){
#			print "index is $index\t k is $k\t m is $m\n";
			$multiple *=$arr[$index];		
#			print "multiple is $multiple\n";
		}
		if ($multiple >= $final){
			$final = $multiple;
		}
#		print "final is : $final\n";
		#$k = $arr[$i+1];
		#$m = $arr[$j+1];
		$j++; $i++;
	}	
	
	print "largest multiple is $final\n";
	
}
&prod(0,12);
		
